import Sender from '../src/email';

const sender = new Sender({
	host: 'smtp.gmail.com',
	port: 465,
	secure: true, // use SSL
	auth: {
			user: 'xxx@gmail.com',
			pass: 'xxx'
	},
	// proxy: 'socks5://localhost:1080/'
	// more options: https://github.com/nodemailer/nodemailer
});

sender.send({
	to: "xxx@163.com", // list of receivers
	subject: '你的任务失败了 🚨', // Subject line
	html: "任务失败。。。"
}).then(() => {
	console.log('发送成功！');
}).catch(err => console.error('发送失败', err));
